import 'package:flutter/material.dart';import 'package:insta_flutter_app/screens/top_page.dart';import 'package:insta_flutter_app/screens/my_page.dart';void main() {  runApp(const MyApp());}class MyApp extends StatelessWidget {  const MyApp({Key? key}) : super(key: key);  // This widget is the root of your application.  @override  Widget build(BuildContext context) {    return MaterialApp(      title: 'Flutter insta clone',      theme: ThemeData(        primarySwatch: Colors.blue,      ),      home: MyStatefulWidget(),    );  }}class MyStatefulWidget extends StatefulWidget {  const MyStatefulWidget({Key? key}) : super(key: key);  @override  State<MyStatefulWidget> createState() => _MyStatefulWidgetState();}class _MyStatefulWidgetState extends State<MyStatefulWidget> {  static const _screens = [    TopPage(),    MyPage(),  ];  int _selectedIndex = 0;  void _onItemTapped(int index) {    setState(() {      _selectedIndex = index;    });  }  @override  Widget build(BuildContext context) {    return Scaffold(        body: _screens[_selectedIndex],        bottomNavigationBar: BottomNavigationBar(          currentIndex: _selectedIndex,          onTap: _onItemTapped,          items: const <BottomNavigationBarItem>[            BottomNavigationBarItem(icon: Icon(Icons.home), label: 'home'),            BottomNavigationBarItem(icon: Icon(Icons.person), label: 'my_page'),          ],          type: BottomNavigationBarType.fixed,        ));  }}